clear all
% options
smootime=3;
mindur=3; %abs cut off for events;


% Befoer SD
cd /home/pinky/Dropbox/Mobs_member/Thierry/DataSommeilSDS_Veronique/WT_Pr√©DS/
[hdr_Pre, recorddata_Pre] = edfread('Traces.edf');

tpsEMG.Pre=[1:length(recorddata_Pre)]/100;
tpsEEG.Pre=[1:length(recorddata_Pre)]/200;

for mousenum = 1:4
emg.Pre{mousenum} = recorddata_Pre(1+(mousenum -1)*3,:);
eog.Pre{mousenum} = recorddata_Pre(2+(mousenum -1)*3,:);
eeg.Pre{mousenum} = recorddata_Pre(3+(mousenum -1)*3,:);
end

% After SD
cd /home/pinky/Dropbox/Mobs_member/Thierry/DataSommeilSDS_Veronique/WT_DSJ1/
[hdr_Post, recorddata_Post] = edfread('Traces.edf');

tpsEMG.Post = [1:length(recorddata_Post)]/100;
tpsEEG.Post = [1:length(recorddata_Post)]/200;

for mousenum = 1:4
emg.Post{mousenum} = recorddata_Post(1+(mousenum -1)*3,:);
eog.Post{mousenum} = recorddata_Post(2+(mousenum -1)*3,:);
eeg.Post{mousenum} = recorddata_Post(3+(mousenum -1)*3,:);
end

% figure, 
% subplot(3,1,1), plot(tpsEMG,emg2), xlim([0 tpsEEG(end)]), ylabel('EMG'),ylim([-1 1]) 
% subplot(3,1,2), plot(tpsEMG,eog2), xlim([0 tpsEEG(end)]), ylabel('EOG'),ylim([-1 1])
% subplot(3,1,3), plot(tpsEEG,eeg2), xlim([0 tpsEEG(end)]), ylabel('EEG'),ylim([-2 2])


% Compare EMG distributions
Conditions = {'Pre','Post'};
clear EMGVals
for mousenum = 1:4
    for c = 1:2
        FilLFP = tsd(tpsEMG.(Conditions{c})*1e4,emg.(Conditions{c}){mousenum}');
        EMGData=tsd(Range(FilLFP),runmean(Data((FilLFP)).^2,ceil(smootime/median(diff(Range(FilLFP,'s'))))));
        tmp = (Data(EMGData));
        tmp(tmp==0) = [];
        EMGVals{mousenum}{c} = log(tmp);
    end
end
clf
for mousenum = 1:4
    subplot(2,2,mousenum)
    nhist(EMGVals{mousenum})
    legend(Conditions)
end

% find gamma epochs
% seuil avec histogramme + propose d'ajustr

plot(EMGVals)

[~,EMG_thresh]=ginput(1);
sleepper=thresholdIntervals(EMGData,EMG_thresh,'Direction','Below');
sleepper=mergeCloseIntervals(sleepper,mindur*1e4);
sleepper=dropShortIntervals(sleepper,mindur*1e4);


FilLFP = tsd(tpsEEG*1e4,eeg2');
